import React from 'react';

import { login } from '../assets/login';


export default class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            submitted: false,
            loading: false,
            error: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password } = this.state;

        // stop here if form is invalid
        if (!(username && password)) {
            return;
        }

        this.setState({ loading: true });
        login(username, password)
            .then((user) => {
                if (user.access_token) {
                    console.log("success")
                    // const { from } = this.props.location.state || { from: { pathname: "/" } };
                    this.props.history.push("/user-surveys");
                }
                else {
                    this.setState({ error: "Credentials Incorrect", loading: false })
                }
            },
            );
    }
    toRegister = () => {
        this.props.history.push("/register");
    }

    render() {
        if (localStorage.getItem('access_token') !== null) {
            this.props.history.push("/user-surveys");
        }
        const { username, password, submitted, loading, error } = this.state;
        return (
            <div className="col-md-6 col-md-offset-3 Login">
                <h2>Login</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <label htmlFor="username">Username</label>
                        <input type="text" className="form-control" style={{ width: "350px" }} name="username" value={username} onChange={this.handleChange} />
                        {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" style={{ width: "350px" }} name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary" disabled={loading}>Login</button>
                        <button className="btn btn-info ml-2" onClick={this.toRegister} inline>Register</button>
                        {loading &&
                            <img src="data:image/gif;base64," alt='some value' />
                        }
                    </div>
                    {error &&
                        <div className={'alert alert-danger'}>{error}</div>
                    }
                </form>
            </div>
        );
    }
}

